	function wpvr_is_theme( $name ) {
		$theme = wp_get_theme();
		
		$possible_names = array(
			strtolower( $theme->stylesheet ),
			strtolower($theme->template ),
			strtolower($theme->parent ),
			strtolower($theme->get( 'Name' ) ),
		);
		
		//d( $name ) ;
		
		return in_array( strtolower($name) , $possible_names );
	}
	
